import React from "react";

const Monday = ({breakfastRecipes}) => {

    const breakfasts = breakfastRecipes;

    const breakfast = breakfasts.map((recipe) => {
        if(recipe.name === 'Sausage And Egg Salad'){
            return(
                <div key={recipe._id}>
                    <p>{recipe.name}</p>
                </div>
            );
        }
    });

    return(
        <>
        <table>
            <tbody>
                <tr>
                    <td className="type" colSpan={4}>{recipe.type}</td>
              </tr>
        {breakfast}
            </tbody>
        </table>
        </>
    );

}

export default Monday;
