import {useState, useEffect} from 'react';
import HappyKetoService from '../services/HappyKetoService';
import Planning from '../components/Planning.js';

const PlanningContainer = () => {

    const [allRecipes, setAllRecipes] = useState([]);

    useEffect(() => {
        HappyKetoService.getRecipes()
        .then(recipes => setAllRecipes(recipes))
    }, []);

    function findRecipeByType(type){
        type = recipe.type
        if(type === "Breakfast"){
            return recipe.type;
        }
    }

    function findBreakfast(recipes){
        const breakfast = recipes.filter(findRecipeByType());
        const indexSausageEggSalad = breakfast.indexOf(recipes.name === "Sausage And Egg Salad");
        breakfast.splice(indexSausageEggSalad, 1);
        return breakfast;
    }

    function findNameSausageAndEggSalad(recipe){
        return recipe.name === "Sausage And Egg Salad"
    }

    function findSausageAndEggSalad(recipes){
        const sausageAndEggSalad = recipes.find(findNameSausageAndEggSalad);
        return sausageAndEggSalad;
    }

    function findTypeDinner(recipe){
        return recipe.type === "Dinner";
    }

    function findDinner(recipes){
        const dinner = recipes.filter(findTypeDinner);
        return dinner;
    }

    const breakfastFoundForWeekEnd = findBreakfast(allRecipes);
    const breakfastFoundForWeek = findSausageAndEggSalad(allRecipes);
    const dinnerFound = findDinner(allRecipes);

    return(
        <div className="main">
            <h1>Your Planning</h1>
            <div className="line"></div>
            <div className="planning-container">
                <Planning breakfastFoundForWeekEnd={breakfastFoundForWeekEnd} breakfastFoundForWeek={breakfastFoundForWeek} dinnerFound={dinnerFound}/>
            </div>
        </div>
    );

}

export default PlanningContainer;
