import React, {useState, useEffect} from 'react';
import Monday from './Monday';

const Planning = ({recipesList, typicalBreakfast, weekendBreakfast, allDinner}) => {

    const [updatedRecipes, setUpdatedRecipes] = useState([]);       // 1
    const [breakfastMonFri, setbreakfastMonFri] = useState([]);     // 2
    const [breakfastList, setBreakfastList] = useState([]);         // 3
    const [dinnerList, setDinnerList] = useState([]);               // 4

    const [monday, setMonday] = useState([]);                       // 5
    // const [tuesday, setTuesday] = useState([]);                     // 6
    // const [wednesday, setWednesday] = useState([]);
    // const [thursday, setThursday] = useState([]);
    // const [friday, setFriday] = useState([]);
    // const [saturday, setSaturday] = useState([]);
    // const [sunday, setSunday] = useState([]);

    useEffect(() => {
        setUpdatedRecipes(recipesList)
    }, [recipesList]);

    useEffect(() => {
        setbreakfastMonFri(typicalBreakfast)
    }, [typicalBreakfast]);

    useEffect(() => {
        setBreakfastList(weekendBreakfast)
    }, [weekendBreakfast]);

    useEffect(() => {
        setDinnerList(allDinner)
    }, [allDinner]);

    const handleClick = function(){
        onClick();
    };

    const onClick = function(){
        findMondayMenu(updatedRecipes);
    }

    const findMondayMenu = function(recipes){
        const dinners = matchDinnerWithTypicalBreakfast(recipes);
        const dinner = new Array(dinners[Math.floor(Math.random() * dinners.length)]);
        removeDinnerFromList(dinner);
        const mondayMenu = [ ...dinner];
        monday.push(mondayMenu)
        setMonday(mondayMenu);
    }

    const matchDinnerWithTypicalBreakfast = function(recipes){
        const dinnerRecipes = dinnerList;
        let possibleDinner = dinnerRecipes
        dinnerRecipes.filter(function(recipe){
            if(recipe.protein > 68.8){
                const index = dinnerRecipes.indexOf(recipe);
                dinnerRecipes.splice(index, 1);
            }
            else if(recipe.carbohydrate > 20.1){
                const index = dinnerRecipes.indexOf(recipe);
                dinnerRecipes.splice(index, 1);
            }
            else if(recipe.fat > 70.9){
                const index = dinnerRecipes.indexOf(recipe);
                dinnerRecipes.splice(index, 1);
            }
            return possibleDinner;
        })
        return possibleDinner;
    }

    const removeDinnerFromList = function(recipe){
        const copiedUpdatedRecipes = [...updatedRecipes];
        copiedUpdatedRecipes.splice(recipe, 1);
        setUpdatedRecipes(copiedUpdatedRecipes);
    };

    return(
        <>
        <button className="submit-plan" onClick={() => {handleClick()}} type="button">Plan Your Week</button>
        <div className="planning-container">
            <div className="planning">
                <table className="table-week">
                    <tbody>
                        <tr>
                            <td className="day">Monday</td>
                            <Monday monday={monday}/>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
        </>
    )

}

export default Planning;
