import React from 'react';
import './Recipes.css';
import RecipeForm from './RecipeForm';

const Recipes = ({recipes, deleteRecipe, updateRecipe, createRecipe}) => {

    const allRecipes = recipes;

    function compareRecipes(recipe1, recipe2){
      if(recipe1.name < recipe2.name){
        return -1;
      }
      if(recipe1.name > recipe2.name){
        return 1;
      }
      return 0;
    }

    const recipesByName = allRecipes.sort(compareRecipes);

    const recipeNodes = recipesByName.map((recipe, index) => {

        const handleDeleteClick = () => {
            deleteRecipe(recipe._id)
        }

        const handleUpdateClick = () => {
          return <div className="recipe-form">
          <form onSubmit={handleSubmit}>
              <table>
                  <tbody>
                      <tr>
                          <td className="type" colSpan={4}>
                              <select name="type" id="type" value={type} required>
                                  <option disabled>Select a type of meal</option>
                                  <option value="Breakfast">Breakfast</option>
                                  <option value="Lunch">Lunch</option>
                                  <option value="Dinner">Dinner</option>
                                  <option value="Dessert">Dessert</option>
                                  <option value="Fish">Fish</option>
                                  <option value="Meat">Meat</option>
                                  <option value="Vegetables">Vegetables</option>
                              </select>
                          </td>
                      </tr>
                      <tr>
                          <td className="title-form" colSpan={4}>
                              <label htmlFor="name" className="name-form">Name</label><br/>
                              <input type="text" id="name" value={name} onChange={handleNameChange} required/>
                          </td>
                      </tr>
                      <tr>
                          <td className="nutrition-form">Protein</td>
                          <td className="nutrition-form">Carbs</td>
                          <td className="nutrition-form">Fat</td>
                          <td className="nutrition-form">Calories</td>
                      </tr>
                      <tr>
                          <td className="grams-form">
                              <input type="number" step="any" id="protein" value={protein} onChange={handleProteinChange} required/>
                          </td>
                          <td className="grams-form">
                              <input type="number" step="any" id="carbohydrate" value={carbohydrate} onChange={handleCarbohydrateChange} required/>
                          </td>
                          <td className="grams-form">
                              <input type="number" step="any" id="fat" value={fat} onChange={handleFatChange} required/>
                          </td>
                          <td className="grams-form">
                              <input type="number" step="any" id="calories" value={calories} onChange={handleCaloriesChange} required/>
                          </td>
                      </tr>
                      <tr>
                          <td className="portions-form" colSpan={4}>
                              <label htmlFor="type">Portion:</label>
                              <input type="number" step="any" id="portion" value={portion} onChange={handlePortionChange} required/>
                          </td>
                      </tr>
                      <tr>
                          <td colSpan={4}>
                              <input className="recipes-button-form" type="submit" name="submit" value="Add Recipe"/>
                          </td>
                      </tr>
                  </tbody>
              </table>
          </form>
      </div>
            // updateRecipe({
            //     _id:recipe._id,
            //     name:recipe.name,
            //     type:recipe.type,
            //     protein:recipe.protein,
            //     carbohydrate:recipe.carbohydrate,
            //     fat:recipe.fat,
            //     calories:recipe.calories,
            //     portion:recipe.portion
            // });
          }

        const portions = () => {
          if(recipe.portion <= 1){
            return recipe.portion + " portion";
          }
          else{
            return recipe.portion + " portions";
          }
        }

        return <div className="recipe" key={index}>
          <table>
            <tbody>
              <tr>
                <td className="type" colSpan={4}>{recipe.type}</td>
              </tr>
              <tr>
                <td className="title" colSpan={4}>{recipe.name}</td>
              </tr>
              <tr>
                <td className="nutrition">Protein</td>
                <td className="nutrition">Carbs</td>
                <td className="nutrition">Fat</td>
                <td className="nutrition">Calories</td>
              </tr>
              <tr>
                <td className="grams">{recipe.protein}</td>
                <td className="grams">{recipe.carbohydrate}</td>
                <td className="grams">{recipe.fat}</td>
                <td className="grams">{recipe.calories}</td>
              </tr>
              <tr>
                <td className="portions" colSpan={4}>{portions()}</td>
              </tr>
              <tr>
                <td colSpan={2}><button className="recipes-button" type="button" onClick={handleUpdateClick}>Edit</button></td>
                <td colSpan={2}><button className="recipes-button" type="button" onClick={handleDeleteClick}>Delete</button></td>
              </tr>
            </tbody>
          </table>
        </div>
    });

    return(
        <>
          {recipeNodes}
        </>
    );

};

export default Recipes;
