import React from "react";

const Monday = ({monday, onDiscardCardClick}) => {

    // const typicalBreakfast = function(breakfastRecipes){
    //     const breakfast = breakfastRecipes.filter(recipe => recipe.name === "Sausage And Egg Salad");
    //     return breakfast;
    // }

    const handleClick = function(){
        onDiscardCardClick();
    };

    const breakfast = monday.map(recipe => {
            return(
                <div key={recipe._id}>
                    <p>{recipe.name}</p>
                    <button onClick={() => {handleClick()}} type="button">&#215;</button>
                </div>
            );
    });

    return(
        <>
        <p>Monday</p>
        <p>{breakfast}</p>
        </>
    );

}

export default Monday;
