import React, {} from 'react';
import './Recipes.css';

const Recipes = ({recipes, deleteRecipe, updateRecipe}) => {

    const allRecipes = recipes;

    function compareRecipes(recipe1, recipe2){
      if(recipe1.name < recipe2.name){
        return -1;
      }
      if(recipe1.name > recipe2.name){
        return 1;
      }
      return 0;
    }

    const recipesByName = allRecipes.sort(compareRecipes);

    const recipeNodes = recipesByName.map((recipe, index) => {

        const handleDeleteClick = () => {
            deleteRecipe(recipe._id)
        }

        const handleUpdateClick = () => {
            updateRecipe({
                _id:recipe._id,
                name:recipe.name,
                type:recipe.type,
                protein:recipe.protein,
                carbohydrate:recipe.carbohydrate,
                fat:recipe.fat,
                calories:recipe.calories,
                portion:recipe.portion
            });
          }

        const portions = () => {
          if(recipe.portion <= 1){
            return recipe.portion + " portion";
          }
          else{
            return recipe.portion + " portions";
          }
        }

        return <div className="recipe" key={index}>
          <table>
            <tr>
              <td className="type" colSpan={4}>{recipe.type}</td>
            </tr>
            <tr>
              <td className="title" colSpan={4}>{recipe.name}</td>
            </tr>
            <tr>
              <th className="nutrition">Protein</th>
              <th className="nutrition">Carbs</th>
              <th className="nutrition">Fat</th>
              <th className="nutrition">Calories</th>
            </tr>
            <tr>
              <td className="grams">{recipe.protein}</td>
              <td className="grams">{recipe.carbohydrate}</td>
              <td className="grams">{recipe.fat}</td>
              <td className="grams">{recipe.calories}</td>
            </tr>
            <tr>
              <td className="portions" colSpan={4}>{portions()}</td>
            </tr>
            <tr>
              <td colSpan={2}><button className="recipes-button" type="button" onClick={handleUpdateClick}>Edit</button></td>
              <td colSpan={2}><button className="recipes-button" type="button" onClick={handleDeleteClick}>Delete</button></td>
            </tr>
          </table>
        </div>
    });

    const [name, setName] = useState("");
    const [portion, setPortion] = useState(1);
    const [type, setType] = useState("");
    const [protein, setProtein] = useState(0);
    const [carbohydrate, setCarbohydrate] = useState(0);
    const [fat, setFat] = useState(0);
    const [calories, setCalories] = useState(0);

    const handleNameChange = event => setName(event.target.value);
    const handlePortionChange = event => setPortion(event.target.value);
    const handleTypeChange = event => setType(event.target.value);
    const handleProteinChange = event => setProtein(event.target.value);
    const handleCarbohydrateChange = event => setCarbohydrate(event.target.value);
    const handleFatChange = event => setFat(event.target.value);
    const handleCaloriesChange = event => setCalories(event.target.value);

    const handleSubmit = event => {
        event.preventDefault();
        const recipe = {
            name,
            portion,
            type,
            protein,
            carbohydrate,
            fat,
            calories
        }
        createRecipe(recipe);
        setName("");
        setPortion();
        setType("");
        setProtein();
        setCarbohydrate();
        setFat();
        setCalories();
        window.location.href = '/recipes';
    };

    return(
        <div className="recipes-container">
        <div className="recipe">
          <form onSubmit={handleSubmit}>
          <table>
            <tr>
              <td className="type" colSpan={4}>Type</td>
            </tr>
            <tr>
              <td className="title" colSpan={4}>Name</td>
            </tr>
            <tr>
              <th className="nutrition">Protein</th>
              <th className="nutrition">Carbs</th>
              <th className="nutrition">Fat</th>
              <th className="nutrition">Calories</th>
            </tr>
            <tr>
              <td className="grams">10</td>
              <td className="grams">12</td>
              <td className="grams">25</td>
              <td className="grams">89</td>
            </tr>
            <tr>
              <td className="portions" colSpan={4}>1 portion</td>
            </tr>
            <tr>
              <td colSpan={4}><button className="recipes-button" type="submit" name="submit">Add Recipe</button></td>
            </tr>
          </table>
          </form>
        </div>
          {recipeNodes}
        </div>
    );

};

export default Recipes;
