import {useState, useEffect} from 'react';
import HappyKetoService from '../services/HappyKetoService';
import Planning from '../components/Planning.js';

const PlanningContainer = () => {

    const [allRecipes, setAllRecipes] = useState([]);

    useEffect(() => {
        HappyKetoService.getRecipes()
        .then(recipes => setAllRecipes(recipes))
    }, []);

    function checkBreakfastMondyToFriday(recipe){
        return recipe.name = 'Sausage And Egg Salad';
    }

    function breakfastMondayToFriday(recipes){
        const breakfast = recipes.find(checkBreakfastMondyToFriday);
        return breakfast;
    }

    function checkDinner(recipe){
        return recipe.type = 'Dinner';
    }

    function dinnerWeek(recipes){
        const dinner = recipes.find(checkDinner);
        return dinner;
    }

    const breakfastWeek = breakfastMondayToFriday(allRecipes);
    const dinnerMonToSun = dinnerWeek(allRecipes);

    return(
        <div className="main">
            <h1>Your Planning</h1>
            <div className="line"></div>
            <div className="planning-container">
                <Planning breakfastWeek={breakfastWeek} d/>
            </div>
        </div>
    );

}

export default PlanningContainer;
