import React from 'react';
import './Recipes.css';

const Recipes = ({recipes, deleteRecipe, updateRecipe}) => {

    const recipeNodes = recipes.map((recipe, index) => {

        const handleDeleteClick = () => {
            deleteRecipe(recipe._id)
        }

        const handleUpdateClick = () => {
            updateRecipe({
                _id:recipe._id,
                name:recipe.name,
                type:recipe.type,
                protein:recipe.protein,
                carbohydrate:recipe.carbohydrate,
                fat:recipe.fat,
                calories:recipe.calories,
                portion:recipe.portion
            });
          }

        const portions = () => {
          if(recipe.portion == 1){
            return recipe.portion + " portion";
          }
          else{
            return recipe.portion + " portions";
          }
        }

        return <li key={index}>
          <table>
            <tr>
              <td className="name" colspan="4">{recipe.name}</td>
            </tr>
            <tr>
              <td className="portions" colspan="4">{portions()}</td>
            </tr>
            <tr>
              <td className="type" colSpan={4}>{recipe.type}</td>
            </tr>
            <tr>
              <th className="nutrition">Protein</th>
              <th className="nutrition">Carbs</th>
              <th className="nutrition">Fat</th>
              <th className="nutrition">Calories</th>
            </tr>
            <tr>
              <td className="grams">{recipe.protein}</td>
              <td className="grams">{recipe.carbohydrate}</td>
              <td className="grams">{recipe.fat}</td>
              <td className="grams">{recipe.calories}</td>
            </tr>
            <tr>
              <td colSpan={2}><button type="button" onClick={handleUpdateClick}>Edit</button></td>
              <td colSpan={2}><button type="button" onClick={handleDeleteClick}>Delete</button></td>
            </tr>
          </table>
        </li>
    });

    return(
        <div>
            <ul>
                {recipeNodes}
            </ul>
        </div>
    );

};

export default Recipes;
