import React from 'react';
import './Recipes.css';

const Recipes = ({recipes, deleteRecipe, updateRecipe}) => {

    const recipeNodes = recipes.map((recipe, index) => {

        const handleDeleteClick = () => {
            deleteRecipe(recipe._id)
        }

        const handleUpdateClick = () => {
            updateRecipe({
                _id:recipe._id,
                name:recipe.name,
                type:recipe.type,
                protein:recipe.protein,
                carbohydrate:recipe.carbohydrate,
                fat:recipe.fat,
                calories:recipe.calories,
                portion:recipe.portion
            });
          }

        const portions = () => {
          if(recipe.portion == 1){
            return recipe.portion + " portion";
          }
          else{
            return recipe.portion + " portions";
          }
        }

        return <li key={index}>
          <table>
            <tr>
              <td colspan="4">{recipe.name}</td>
            </tr>
            <tr>
              <td colspan="2">{recipe.type}</td>
              <td colspan="2">{portions()}</td>
            </tr>
            <tr>
              <th className="nutrition">Protein</th>
              <th className="nutrition">Carbs</th>
              <th className="nutrition">Fat</th>
              <th className="nutrition">Calories</th>
            </tr>
            <tr>
              <td className="nutrition">{recipe.protein}&nbsp;g</td>
              <td className="nutrition">{recipe.carbohydrate}&nbsp;g</td>
              <td>{recipe.fat}&nbsp;g</td>
              <td>{recipe.calories}&nbsp;g</td>
            </tr>
          </table>
          <button type="button" onClick={handleUpdateClick}>Edit</button>
          <button type="button" onClick={handleDeleteClick}>Delete</button>
        </li>
    });

    return(
        <div>
            <ul>
                {recipeNodes}
            </ul>
        </div>
    );

};

export default Recipes;
