import React, {useState, useEffect} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import NavBar from './components/NavBar';
import RecipesContainer from './containers/RecipesContainer';
import PlanningContainer from './containers/PlanningContainer';
import ErrorPage from './components/ErrorPage';

const App = ()=> {

  const [recipes, setAllRecipes] = useState([]);

    useEffect(() => {
        HappyKetoService.getRecipes()
        .then(recipes => setAllRecipes(recipes))
    }, []);

  return (
    <Router>
        <NavBar/>
        <Switch>
          <Route exact path="/" render={() => <PlanningContainer recipes={recipes}/>}/>     
          <Route path="/recipes" render={() => <RecipesContainer recipes={recipes}/>}/>
          <Route component={ErrorPage}/>
        </Switch>
    </Router>
  );

};

export default App;
