import {useState, useEffect} from 'react';
import HappyKetoService from '../services/HappyKetoService';
import Planning from '../components/Planning.js';

const PlanningContainer = () => {

    const [allRecipes, setAllRecipes] = useState([]);

    useEffect(() => {
        HappyKetoService.getRecipes()
        .then(recipes => setAllRecipes(recipes))
    }, []);

    function typeBreakfast(recipe){
        return recipe.type === "Breakfast";
    }

    function findBreakfast(recipes){
        let breakfastList = [];
        const breakfast = recipes.filter(typeBreakfast);
        breakfastList.push(breakfast);
        return breakfastList;
    }

    const breakfastFound = findBreakfast(allRecipes);

    return(
        <div className="main">
            <h1>Your Planning</h1>
            <div className="line"></div>
            <div className="planning-container">
                <Planning breakfastFound={breakfastFound}/>
            </div>
        </div>
    );

}

export default PlanningContainer;
