import {useState} from 'react';

const RecipeForm = ({createRecipe}) => {

    const [name, setName] = useState("");
    const [portion, setPortion] = useState(1);
    const [type, setType] = useState("");
    const [protein, setProtein] = useState(0);
    const [carbohydrate, setCarbohydrate] = useState(0);
    const [fat, setFat] = useState(0);
    const [calories, setCalories] = useState(0);

    const handleNameChange = event => setName(event.target.value);
    const handlePortionChange = event => setPortion(event.target.value);
    const handleTypeChange = event => setType(event.target.value);
    const handleProteinChange = event => setProtein(event.target.value);
    const handleCarbohydrateChange = event => setCarbohydrate(event.target.value);
    const handleFatChange = event => setFat(event.target.value);
    const handleCaloriesChange = event => setCalories(event.target.value);

    const handleSubmit = event => {
        event.preventDefault();
        const recipe = {
            name,
            portion,
            type,
            protein,
            carbohydrate,
            fat,
            calories
        }
        createRecipe(recipe);
        setName("");
        setPortion();
        setType("");
        setProtein();
        setCarbohydrate();
        setFat();
        setCalories();
        window.location.href = '/recipes';
    };

    return(
        <form onSubmit={handleSubmit}>
            <table>
                <tr>
                    <td className="type" colSpan={4}>
                        <select name="type" id="type" value={type} onChange={handleTypeChange} required>
                            <option value="Breakfast">Breakfast</option>
                            <option value="Lunch">Lunch</option>
                            <option value="Dinner">Dinner</option>
                            <option value="Dessert">Dessert</option>
                            <option value="Fish">Fish</option>
                            <option value="Meat">Meat</option>
                            <option value="Vegetables">Vegetables</option>
                        </select>
                    </td>
                </tr>
                <tr>
                    <td className="name" colSpan={4}>
                        <label htmlFor="name">Name</label><br/>
                        <input type="text" id="name" value={name} onChange={handleNameChange} required/>
                    </td>
                </tr>
                <tr>
                    <th className="nutrition">Protein</th>
                    <th className="nutrition">Carbs</th>
                    <th className="nutrition">Fat</th>
                    <th className="nutrition">Calories</th>
                </tr>
                <tr>
                    <td className="grams">
                        <input type="number" step="any" id="protein" value={protein} onChange={handleProteinChange} required/>
                    </td>
                    <td className="grams">
                        <input type="number" step="any" id="carbohydrate" value={carbohydrate} onChange={handleCarbohydrateChange} required/>
                    </td>
                    <td className="grams">
                    <input type="number" step="any" id="fat" value={fat} onChange={handleFatChange} required/>
                    </td>
                <td className="grams">{recipe.calories}</td>
                </tr>
                <tr>
                <td className="portions" colSpan={4}>{portions()}</td>
                </tr>
            </table>
            <p><label htmlFor="name">Name</label></p>
            <p><input type="text" id="name" value={name} onChange={handleNameChange} required/></p>
            <p><label htmlFor="type">Portion</label></p>
            <p><input type="number" step="any" id="portion" value={portion} onChange={handlePortionChange} required/></p>
            <p><label htmlFor="type">Type</label></p>
            <p><select name="type" id="type" value={type} onChange={handleTypeChange} required>
                <option value="Breakfast">Breakfast</option>
                <option value="Lunch">Lunch</option>
                <option value="Dinner">Dinner</option>
                <option value="Dessert">Dessert</option>
                <option value="Fish">Fish</option>
                <option value="Meat">Meat</option>
                <option value="Vegetables">Vegetables</option>
            </select></p>
            <p><label htmlFor="type">Protein</label></p>
            <p><input type="number" step="any" id="protein" value={protein} onChange={handleProteinChange} required/></p>
            <p><label htmlFor="type">Carbohydrate</label></p>
            <p><input type="number" step="any" id="carbohydrate" value={carbohydrate} onChange={handleCarbohydrateChange} required/></p>
            <p><label htmlFor="type">Fat</label></p>
            <p><input type="number" step="any" id="fat" value={fat} onChange={handleFatChange} required/></p>
            <p><label htmlFor="type">Calories</label></p>
            <p><input type="number" step="any" id="calories" value={calories} onChange={handleCaloriesChange} required/></p>
            <p><input type="submit" name="submit" value="Add Recipe"/></p>
        </form>
    );

};

export default RecipeForm;
