import {useState} from 'react';

const RecipeForm = ({createRecipe}) => {

    const [name, setName] = useState("");
    const [portion, setPortion] = useState();
    const [type, setType] = useState("");
    const [protein, setProtein] = useState();
    const [carbohydrate, setCarbohydrate] = useState();
    const [fat, setFat] = useState();
    const [calories, setCalories] = useState();

    const handleNameChange = event => setName(event.target.value);
    const handlePortionChange = event => setPortion(event.target.value);
    const handleTypeChange = event => setType(event.target.value);
    const handleProteinChange = event => setProtein(event.target.value);
    const handleCarbohydrateChange = event => setCarbohydrate(event.target.value);
    const handleFatChange = event => setFat(event.target.value);
    const handleCaloriesChange = event => setCalories(event.target.value);

    const handleSubmit = event => {
        event.preventDefault();
        const recipe = {
            name,
            portion,
            type,
            protein,
            carbohydrate,
            fat,
            calories
        }
        createRecipe(recipe);
        setName("");
        setPortion();
        setType("");
        setProtein();
        setCarbohydrate();
        setFat();
        setCalories();
    };

    return(
        <form onSubmit={handleSubmit}>
            <label htmlFor="name">Name</label>
            <input type="text" id="name" value={name} onChange={handleNameChange} required/>
            <label htmlFor="type">Portion</label>
            <input type="number" step="any" id="portion" value={portion} onChange={handlePortionChange} required/>
            <label htmlFor="type">Type</label>
            <input type="text" id="type" value={type} onChange={handleTypeChange} required/>
            <label htmlFor="type">Protein</label>
            <input type="number" id="protein" value={protein} onChange={handleProteinChange} required/>
            <label htmlFor="type">carbohydrate</label>
            <input type="text" id="carbohydrate" value={carbohydrate} onChange={handleCarbohydrateChange} required/>
            <label htmlFor="type">Fat</label>
            <input type="text" id="fat" value={fat} onChange={handleFatChange} required/>
            <label htmlFor="type">Calories</label>
            <input type="text" id="calories" value={calories} onChange={handleCaloriesChange} required/>
            <input type="submit" name="submit" value="Add Recipe"/>
        </form>
    );

};

export default RecipeForm;
