import {useState, useEffect} from 'react';
import HappyKetoService from '../services/HappyKetoService';
import Planning from '../components/Planning.js';

const PlanningContainer = () => {

    const [recipes, setAllRecipes] = useState([]);
    const [days, setDays] = useState({Monday: [], Tuesday: [], Wednesday: [], Thursday: [], Friday: [], Saturday: [], Sunday: []});

    useEffect(() => {
        HappyKetoService.getRecipes()
        .then(recipes => setAllRecipes(recipes))
    }, []);

    function breakfastWeek(recipes){
        const breakfast = recipes.name === "Sausage And Egg Salad";
        setDays(breakfast);
    }

    const breakfast = breakfastWeek(recipes);

    return(
        <div className="main">
            <h1>Your Planning</h1>
            <div className="line"></div>
            <div className="planning-container">
                <Planning days={days} breakfast={breakfast}/>
            </div>
        </div>
    );

}

export default PlanningContainer;
