import React from "react";

const Monday = ({breakfastRecipes}) => {

    const breakfasts = breakfastRecipes;

    const typicalBreakfast = function(recipes){
        const sausageAndEggSalad = recipes.find(recipe => recipe.name === "Sausage And Egg Salad");
        return sausageAndEggSalad;
    }

    // const breakfast = typicalBreakfast(breakfasts);

    const breakfast = breakfasts.map((recipe) => {
        return(
            <div key={recipe._id}>
                <p>{recipe.name}</p>
            </div>
        );
    });

    return(
        <>
        <p>Monday Menu</p>
        {breakfast}
        </>
    );

}

export default Monday;
