                            <Monday monday={monday} onClick={onClick}/>
                    </tbody>
                </table>
            </div>
            <div className='planning'>
                <table className='table-week'>
                    <tbody>
                        <tr>
                            <th className='day' colSpan={3}>Thursday</th>
                        </tr>
                            <Monday monday={monday} onClick={onClick}/>
                    </tbody>
                </table>
            </div>
            <div className='planning'>
                <table className='table-week'>
                    <tbody>
                        <tr>
                            <th className='day' colSpan={3}>Friday</th>
                        </tr>
                            <Monday monday={monday} onClick={onClick}/>
                    </tbody>
                </table>
            </div>
            <div className='planning'>
                <table className='table-week'>
                    <tbody>
                        <tr>
                            <th className='day' colSpan={3}>Saturday</th>
                        </tr>
                            <Monday monday={monday} onClick={onClick}/>
                    </tbody>
                </table>
            </div>
            <div className='planning'>
                <table className='table-week'>
                    <tbody>
                        <tr>
                            <th className='day' colSpan={3}>Sunday</th>
                        </tr>
                            <Monday monday={monday} onClick={onClick}/>
                    </tbody>
                </table>
            </div>
        </div>
        </>
    )

}

export default Planning;
